import React from 'react';
import '../pages/Inicio.css';

import { useEffect, useRef, useState } from 'react';

const frases = [
  'Bem-vindo ao Meu Portfólio',
  'Téc.Desenvolvimento de Sistemas',
  'Desenvolvedor FrontEnd',
  'Programador de microcontroladores'
];

function Inicio() {
  const [text, setText] = useState('');
  const [fraseIndex, setFraseIndex] = useState(0);
  const [digitando, setDigitando] = useState(true);
  const charIndex = useRef(0);

  useEffect(() => {
    setText('');
    charIndex.current = 0;
    setDigitando(true);
    const intervalo = setInterval(() => {
      if (charIndex.current < frases[fraseIndex].length) {
        setText((prev) => prev + frases[fraseIndex][charIndex.current]);
        charIndex.current++;
      } else {
        setDigitando(false);
        clearInterval(intervalo);
        setTimeout(() => {
          // Apagar texto
          const apagar = setInterval(() => {
            setText((prev) => {
              if (prev.length > 0) {
                return prev.slice(0, -1);
              } else {
                clearInterval(apagar);
                setFraseIndex((prevIndex) => (prevIndex + 1) % frases.length);
                setDigitando(true);
                return '';
              }
            });
          }, 30);
        }, 1200);
      }
    }, 90);
    return () => clearInterval(intervalo);
  }, [fraseIndex]);

  return (
    <div className="inicio-container">
      <div className="background-animation"></div>
      <h1 className="typewriter">{text}<span className="cursor">|</span></h1>
      <p>Este é o início do seu site em React. Use a navegação para conhecer mais!</p>
    </div>
  );
}


export default Inicio;
