import React from 'react';
import './Inicio.css';

import { useEffect, useRef, useState } from 'react';

const frases = [
  'Bem-vindo ao Meu Portfólio',
  'Téc.Desenvolvimento de Sistemas',
  'Desenvolvedor FrontEnd',
  'Programador de microcontroladores'
];

function Inicio() {
  const [text, setText] = useState('');
  const [fraseIndex, setFraseIndex] = useState(0);
  const [digitando, setDigitando] = useState(true);

  useEffect(() => {
    let typingTimeout;
    let deletingTimeout;

    if (digitando) {
      if (text.length < frases[fraseIndex].length) {
        typingTimeout = setTimeout(() => {
          setText(frases[fraseIndex].slice(0, text.length + 1));
        }, 90);
      } else {
        setTimeout(() => setDigitando(false), 1200);
      }
    } else {
      if (text.length > 0) {
        deletingTimeout = setTimeout(() => {
          setText(frases[fraseIndex].slice(0, text.length - 1));
        }, 30);
      } else {
        setDigitando(true);
        setFraseIndex((prev) => (prev + 1) % frases.length);
      }
    }

    return () => {
      clearTimeout(typingTimeout);
      clearTimeout(deletingTimeout);
    };
  }, [text, digitando, fraseIndex]);

  const [reveal, setReveal] = useState(false);
  const [particles, setParticles] = useState([]);
  const [mouse, setMouse] = useState({ x: null, y: null });
  const bgRef = useRef();

  useEffect(() => {
    setTimeout(() => setReveal(true), 200);
  }, []);

  useEffect(() => {
    // Listener para atualizar posição do mouse relativa ao background
    const handleMouseMove = (e) => {
      if (!bgRef.current) return;
      const rect = bgRef.current.getBoundingClientRect();
      setMouse({
        x: ((e.clientX - rect.left) / rect.width) * 100,
        y: ((e.clientY - rect.top) / rect.height) * 100
      });
    };
    if (bgRef.current) {
      bgRef.current.addEventListener('mousemove', handleMouseMove);
      bgRef.current.addEventListener('mouseleave', () => setMouse({ x: null, y: null }));
    }
    return () => {
      if (bgRef.current) {
        bgRef.current.removeEventListener('mousemove', handleMouseMove);
        bgRef.current.removeEventListener('mouseleave', () => setMouse({ x: null, y: null }));
      }
    };
  }, []);

  useEffect(() => {
    // Criar partículas
    const createParticles = () => {
      const newParticles = [];
      const numParticles = 100;

      for (let i = 0; i < numParticles; i++) {
        newParticles.push({
          id: i,
          size: Math.random() * 6 + 3,
          left: Math.random() * 100,
          top: Math.random() * 100,
          duration: Math.random() * 5 + 4, // mais rápido
          delay: Math.random(), // delay menor
          opacity: Math.random() * 0.5 + 0.1,
          moveX: (Math.random() - 5) * 60,
          moveY: (Math.random() - 5) * 60
        });
      }

      setParticles(newParticles);
    };

    createParticles();

    // Recriar partículas periodicamente para manter o efeito
    const interval = setInterval(() => {
      createParticles();
    }, 30000); // A cada 30 segundos

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="inicio-container">
      <div className="background-animation" ref={bgRef}>
        {particles.map((particle) => {
          let repelX = 0;
          let repelY = 0;
          if (mouse.x !== null && mouse.y !== null && bgRef.current) {
            const rect = bgRef.current.getBoundingClientRect();
            // Posição do mouse em px
            const mousePx = {
              x: (mouse.x / 100) * rect.width,
              y: (mouse.y / 100) * rect.height
            };
            // Posição da partícula em px
            const particlePx = {
              x: (particle.left / 100) * rect.width,
              y: (particle.top / 100) * rect.height
            };
            const dx = particlePx.x - mousePx.x;
            const dy = particlePx.y - mousePx.y;
            const dist = Math.sqrt(dx * dx + dy * dy);
            const repelRadiusPx = 20;
            if (dist < repelRadiusPx) {
              // Move exatamente para a borda do círculo
              const angle = Math.atan2(dy, dx);
              const newX = mousePx.x + Math.cos(angle) * repelRadiusPx;
              const newY = mousePx.y + Math.sin(angle) * repelRadiusPx;
              repelX = newX - particlePx.x;
              repelY = newY - particlePx.y;
            }
          }
          return (
            <div
              key={particle.id}
              className="particle"
              style={{
                width: `${particle.size}px`,
                height: `${particle.size}px`,
                left: `calc(${particle.left}% + ${repelX}px)`,
                top: `calc(${particle.top}% + ${repelY}px)`,
                '--duration': `${particle.duration}s`,
                '--delay': `${particle.delay}s`,
                '--max-opacity': particle.opacity,
                '--move-x': `${particle.moveX}px`,
                '--move-y': `${particle.moveY}px`
              }}
            />
          );
        })}
      </div>
      <h1 className={`typewriter${reveal ? ' reveal' : ''}`}>{text}<span className="cursor">|</span></h1>
      <p className={reveal ? 'reveal' : ''}>Este é o início do seu site em React. Use a navegação para conhecer mais!</p>
    </div>
  );
}



export default Inicio;
