import React from 'react';
import '../pages/Inicio.css';

import { useEffect, useRef, useState } from 'react';

function Inicio() {
  const [text, setText] = useState('');
  const fullText = 'Bem-vindo ao Meu Portfólio';
  const index = useRef(0);

  useEffect(() => {
    setText('');
    index.current = 0;
    const interval = setInterval(() => {
      setText((prev) => prev + fullText[index.current]);
      index.current++;
      if (index.current === fullText.length) clearInterval(interval);
    }, 90);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="inicio-container">
      <div className="background-animation"></div>
      <h1 className="typewriter">{text}<span className="cursor">|</span></h1>
      <p>Este é o início do seu site em React. Use a navegação para conhecer mais!</p>
    </div>
  );
}

export default Inicio;
